import './cards.css';
import star from './../../img/icons/star.svg';

const Cards = (props) =>{


    function component(oldprice) {
        if (oldprice) {
          const newOldPrice = oldprice + '\u20BD';
          return <div>{newOldPrice}</div>;
        }
        return null;
      }

    console.log(props)
    return (
        <div className='cards'>
            <img className='cards__img' src={props.img} alt={props.title}></img>
            <div className='cards__body'>
                <div className='cards__title'>{props.title}</div>
                <div className='cards__price'>
                    <span className='cards__price__actual'>{props.price}</span>
                    <span className='cards__price__discont'>{component(props.oldprice)}</span>
                </div>
                <div className='cards__raiting'>
                    <img className='cards__raiting__icon' src={star} alt='рейтинг'></img>
                    <span className='cards__raiting__text'>{props.rate}</span>
                </div>
                <div className='cards__buy'>Купить</div>
            </div>
        </div>
    )
}

export default Cards;
