import React, { useState } from 'react';

import './cards.css';
import star from './../../img/icons/star.svg';












const Cards = (props) =>{


    const [cartItems, setCartItems] = useState([]); // Список выбранных товаров
    const [cartItemCount, setCartItemCount] = useState(0); // Счетчик товаров в корзине
    const addToCart = (product) => {
        setCartItems([...cartItems, product]); // Добавление товара в список выбранных товаров
        setCartItemCount(cartItemCount + 1); // Увеличение счетчика товаров
    }


    const checkOldPrice = (oldprice) => {
        if (oldprice) {
          const newOldPrice = oldprice + '\u20BD';
          return <span className='cards__price__discont'>{oldprice}</span>;
        }
        return null;
      }

    console.log(props)

    return (
        <div className='cards'>
            <div className='cards__img__block'>
                <img className='cards__img' src={props.img} alt={props.title}></img>
            </div>
            <div className='cards__body'>
                <div className='cards__title'>{props.title}</div>
                <div className='cards__price'>
                    <span className='cards__price__actual'>{props.price}</span>
                    {checkOldPrice(props.oldprice)}
                </div>
                <div className='cards__raiting'>
                    <img className='cards__raiting__icon' src={star} alt='рейтинг'></img>
                    <span className='cards__raiting__text'>{props.rate}</span>
                </div>
                <div className='cards__buy'>Купить</div>
            </div>
            <div>
      {/* Рендеринг компонента cards и передача функции addToCart в качестве пропса */}
      <Cards addToCart={addToCart} />
      <div>Количество товаров в корзине: {cartItemCount}</div>
    </div>
        </div>
    )
}

export default Cards;
