import './regiontop.css';
import Cards from './../cards/Cards';
import product from '../../headphones';
import { useState } from 'react';

const RegionTop = () =>{

    const [cartHeadphones, setCart] = useState([]);

    const addToCart = (product) => {
        setCart(prevCart => [...prevCart, product]);  //создает новый массив, копируя все элементы предыдущего и добавляя новый

    }

    //console.log(cartHeadphones)

const jsonArrayBasket = JSON.stringify(cartHeadphones)

sessionStorage.setItem('myObject',jsonArrayBasket)

 //console.log(jsonArrayBasket)


    return (
        <div className='region__top'>
            <div className='region__top__title'>
            Наушники
            </div>
            <div className='region__top__cards'>
                {product.map((product) => (
                    <Cards key={product.id} id={product.id} title={product.title} img={product.img} price={product.price} oldprice={product.oldPrice} rate={product.rate} count={product.count} addToCart={addToCart} />
                ))}
            </div>

        </div>
    )
}

export default RegionTop;
