import './header.css'
import heartImg from './../../img/icons/heart.svg'
import basketImg from './../../img/icons/basket.svg'
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';


function Header(){   

   // const [cartHeadphones, setCart] = useState([]);

  //let basketLength = sessionStorage.getItem('basketLength');






  const [basketLength, setBasketLength] = useState(sessionStorage.getItem('basketLength') || 0);

  useEffect(() => {
    // Установите обработчик события изменения на sessionStorage
    const handleStorageChange = () => {
      setBasketLength(sessionStorage.getItem('basketLength') || 0);
    };

    // Добавьте обработчик события изменения на sessionStorage
    window.addEventListener('storage', handleStorageChange);

    // Удалите обработчик события при размонтировании компонента
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []); // useEffect без зависимостей, так как мы хотим слушать изменения в sessionStorage





    return(        
        <header className='header'>
            <div className='container'>
                <div className='header__row'>
                    <Link to="/"><div className='header__logo' title='Вернуться на главную'>QPICK</div></Link>                    
                    <div className='header__icons'>
                        <div className='header__icons__heart'>
                            <Link to="/"><img src={heartImg} alt="Добавить в желаемое" title="Добавить в желаемое" /></Link> 
                            <span id='countHeart'>1</span>
                        </div>
                        <div className='header__icons__basket'>
                            <Link to="/basket"><img src={basketImg} alt='Корзина' title='Корзина' /></Link>  
                            <span id='countBasket'>{basketLength}</span>
                        </div>
                    </div>
                </div>
            </div>
        </header>
    )
}
export default Header;
