import './header.css'
import heartImg from './../../img/icons/heart.svg'
import basketImg from './../../img/icons/basket.svg'
import { Link } from 'react-router-dom';
import React, { useState } from 'react';
import Cards from '../cards/Cards';




function Header(){   


    const [basketLength, setBasketLength] = useState(0);

    // Обратный вызов для обновления basketLength из компонента Cards
    const updateBasketLength = (newLength) => {
      setBasketLength(newLength);
    };

//let basketLength = JSON.parse(sessionStorage.getItem('cartIds')) === null ? 0 : JSON.parse(sessionStorage.getItem('cartIds')).length;

    return(        
        <header className='header'>
            <div className='container'>
                <div className='header__row'>
                    <Link to="/"><div className='header__logo' title='Вернуться на главную'>QPICK</div></Link>                    
                    <div className='header__icons'>
                        <div className='header__icons__heart'>
                            <Link to="/"><img src={heartImg} alt="Добавить в желаемое" title="Добавить в желаемое" /></Link> 
                            <span id='countHeart'>1</span>
                        </div>
                        <div className='header__icons__basket'>
                            <Link to="/basket"><img src={basketImg} alt='Корзина' title='Корзина' /></Link>  
                            <span id='countBasket'>{basketLength}</span>
                        </div>
                    </div>
                </div>
            </div>

            <Cards updateBasketLength={updateBasketLength} />

        </header>
    )
}
export default Header;
