import './regionbasket.css'
import img from './../../img/previews/Apple BYZ S852I.svg'
import imgIncrement from './../../img/icons/increment.svg'
import imgDecrement from './../../img/icons/decrement.svg'
import imgDelete from './../../img/icons/delete.svg'
import Count from '../count/Count'
import ButtonDelete from '../buttonDelete/ButtonDelete'
import RegionBasketCards from './RegionBasketCards'

import headphones from '../../headphones'
import wirelessHeadphones from "../../wirelessHeadphones";


import { useState } from "react";

const Regionbasket = () =>{



    const [cart, setCart] = useState(headphones);

    const deleteProduct = (id) =>{
            console.log('delete', id)
    }


    const products = cart.map((product)=>{
     return <RegionBasketCards product={product} key={product.id} deleteProduct={deleteProduct}/>
    })





    return(
        <div className='basket'>
            <div className='basket__title'>Корзина</div>

            <div className='basket__block_and_price'>
                <div className='basket__block'>

                    {products}


                </div>
                <div className='basket__result__price'>
                    <div className='basket__result_price__text'>ИТОГО</div>
                    <div className='basket__result__price__price'>0</div>
                    <div className='basket__result__price__pay'>Перейти к оформлению</div>
                </div>
            </div>
        </div>
    )
}
export default Regionbasket;
